import React from 'react';

const SkinHairCare = () => {
  const skinCareRecipes = [
    {
      name: 'Turmeric Face Pack',
      ingredients: ['2 tbsp gram flour (besan)', '1/2 tsp turmeric powder', '2-3 drops rose water', 'Milk/yogurt as needed'],
      method: 'Mix all ingredients to form a paste. Apply evenly on face and neck. Leave for 15-20 minutes and rinse with lukewarm water.',
      benefits: 'Anti-inflammatory, brightening, and acne-fighting properties',
    },
    {
      name: 'Kumkumadi Face Oil',
      ingredients: ['Saffron', 'Sandalwood powder', 'Rose petals', 'Sesame oil base'],
      method: 'Traditional preparation involves infusing ingredients in base oil under sunlight for specific duration.',
      benefits: 'Brightens complexion, reduces pigmentation, and promotes skin radiance',
    },
    {
      name: 'Saffron Glow Pack',
      ingredients: ['Saffron', 'Raw milk', 'Honey'],
      method: 'Mix all ingredients and apply for 20 minutes before bedtime. Rinse with warm water.',
      benefits: 'Brightens skin and evens tone',
    }
  ];

  const hairCareRecipes = [
    {
      name: 'Kerala Hair Oil Massage',
      ingredients: ['Coconut oil', 'Curry leaves', 'Fenugreek seeds', 'Amla powder'],
      method: 'Heat coconut oil with ingredients, strain, and cool. Massage into scalp and leave overnight.',
      benefits: 'Promotes hair growth, prevents premature graying, strengthens roots',
    },
    {
      name: 'Hibiscus Hair Mask',
      ingredients: ['Hibiscus flowers/leaves', 'Yogurt', 'Aloe vera gel', 'Coconut oil'],
      method: 'Blend ingredients into a smooth paste. Apply from roots to tips, leave for 30 minutes, then wash.',
      benefits: 'Prevents hair fall, adds shine, promotes growth',
    }
  ];

  return (
    <div className="container mx-auto px-4 py-8"
      style={{
        backgroundImage: 'url("/images/home-bg.jpg")',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundAttachment: 'fixed'
      }}>
      <h1 className="text-3xl font-bold text-purple-800 mb-8">Kerala Ayurveda Skin & Hair Care</h1>


    </div>
  );
};

export default SkinHairCare;
