import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';

const Sidebar = () => {
  const location = useLocation();
  const [isOpen, setIsOpen] = useState(false);

  // Close sidebar when route changes on mobile
  useEffect(() => {
    setIsOpen(false);
  }, [location]);

  const navItems = [
    { path: '/period-health', label: 'Period Health' },
    { path: '/mental-wellness', label: 'Mental Wellness' },
    { path: '/sexual-health', label: 'Sexual & Reproductive Health' },
    { path: '/skin-hair-care', label: 'Skin & Hair Care' },
    { path: '/pregnancy-care', label: 'Pregnancy Care' },
  ];

  return (
    <>
      {/* Mobile menu button */}
      <button
        onClick={() => setIsOpen(!isOpen)}
        className="md:hidden fixed top-4 left-4 z-40 p-2 rounded-md bg-purple-600 text-white"
      >
        <span className="sr-only">Open menu</span>
        <svg
          className="h-6 w-6"
          xmlns="http://www.w3.org/2000/svg"
          fill="none"
          viewBox="0 0 24 24"
          stroke="currentColor"
        >
          {isOpen ? (
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
          ) : (
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
          )}
        </svg>
      </button>

      <div className={`bg-white w-64 space-y-6 py-7 px-2 fixed inset-y-0 left-0 transform ${
        isOpen ? 'translate-x-0' : '-translate-x-full'
      } md:relative md:translate-x-0 transition duration-200 ease-in-out shadow-lg z-30`}>
      <Link to="/" className="text-xl sm:text-2xl font-semibold text-center text-purple-600 flex items-center px-3 sm:px-4">
        Swasthyashe
      </Link>
      <nav>
        {navItems.map((item) => (
          <Link
            key={item.path}
            to={item.path}
            className={`flex items-center space-x-2 py-2 sm:py-2.5 px-3 sm:px-4 rounded transition duration-200 text-sm sm:text-base ${
              location.pathname === item.path ? 'bg-purple-100 text-purple-700' : 'text-gray-700 hover:bg-purple-50'
            }`}
          >
            {item.label}
          </Link>
        ))}
      </nav>
    </div>

      {/* Overlay */}
      <div
        className={`fixed inset-0 bg-black bg-opacity-50 z-20 md:hidden ${
          isOpen ? 'block' : 'hidden'
        }`}
        onClick={() => setIsOpen(false)}
      />
    </>
  );
};

export default Sidebar;
