import React from "react";

const remedies = [
  {
    title: "Yoga for Period Cramps",
    video: "https://www.youtube.com/embed/VIDEO_ID1",
    image: "/images/yoga.png",
    description: "Gentle yoga poses can ease cramps and improve circulation.",
  },
  {
    title: "Heat Therapy",
    video: "https://www.youtube.com/embed/VIDEO_ID2",
    image: "/images/heat-pad.png",
    description: "Applying a heating pad can relax muscles and relieve pain.",
  },
  {
    title: "Herbal Teas",
    video: "https://www.youtube.com/embed/VIDEO_ID3",
    image: "/images/herbal-tea.png",
    description: "Chamomile and ginger tea help reduce inflammation and discomfort.",
  },
];

const NaturalRemedies = () => {
  return (
    <div className="container mx-auto px-4 py-10">
      <h2 className="text-2xl font-bold text-center mb-6">Natural Remedies for Period Pain</h2>
      <div className="grid md:grid-cols-2 gap-6">
        {remedies.map((remedy, index) => (
          <div key={index} className="bg-white shadow-lg rounded-lg p-4 flex flex-col">
            <iframe 
              src={remedy.video}
              title={remedy.title}
              className="w-full h-48 rounded-lg"
              allowFullScreen
            ></iframe>
            <div className="flex items-center mt-4">
              <img src={remedy.image} alt={remedy.title} className="w-20 h-20 rounded-lg object-cover mr-4" />
              <div>
                <h3 className="text-lg font-semibold">{remedy.title}</h3>
                <p className="text-sm text-gray-600">{remedy.description}</p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default NaturalRemedies;
