import React from "react";
import { Bar } from "react-chartjs-2";
import { Chart, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from "chart.js";

Chart.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const DietPlan = () => {
  const data = {
    labels: ["Underweight", "Normal weight", "Overweight", "Obese"],
    datasets: [
      {
        label: "Recommended Weight Gain (kg)",
        data: [15, 13.75, 9.25, 7], // Average values
        backgroundColor: ["#8e44ad", "#9b59b6", "#d2b4de", "#e8daef"],
        borderWidth: 1,
      },
    ],
  };

  const options = {
    responsive: true,
    maintainAspectRatio: false,
    scales: {
      y: {
        beginAtZero: true,
      },
    },
  };

  return (
    <div 
      className="container mx-auto px-4 py-8"
      style={{ 
        backgroundImage: 'url("/images/home-bg.jpg")',
        backgroundBlendMode: "multiply",
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundAttachment: "fixed",
      }}
    >
      <h1 className="text-xl font-semibold text-center text-purple-700">
        Healthy Diet During Pregnancy
      </h1>

      <h2 className="text-lg font-semibold text-purple-700 mt-6">Healthy Weight Gain During Pregnancy</h2>
      <p>Steady weight gain is essential for both the baby’s development and the mother's health.</p>

      <h3 className="text-lg font-semibold text-purple-700 mt-4">Importance of Healthy Weight Gain</h3>
      <ul className="list-disc list-inside">
        <li>Supports baby’s growth and development.</li>
        <li>Essential for maternal health and postpartum recovery.</li>
      </ul>

      <h3 className="text-lg font-semibold text-purple-700 mt-4">Risks of Excess Weight Gain</h3>
      <ul className="list-disc list-inside">
        <li>Increases chances of gestational diabetes and high blood pressure.</li>
        <li>Can make it harder to lose weight after pregnancy.</li>
      </ul>

      <h3 className="text-lg font-semibold text-purple-700 mt-4">Recommended Weight Gain (Based on BMI)</h3>
      <ul className="list-disc list-inside">
        <li><b>Underweight (BMI &lt; 18.5)</b> → 12.5 - 18 kg</li>
        <li><b>Normal weight (BMI 18.5 - 24.9)</b> → 11.5 - 16 kg</li>
        <li><b>Overweight (BMI 25 - 29.9)</b> → 7 - 11.5 kg</li>
        <li><b>Obese (BMI ≥ 30)</b> → 5 - 9 kg</li>
      </ul>

      <div className="mt-6">
        <h3 className="text-lg font-semibold text-purple-700">Weight Gain Chart</h3>
        <div className="w-full h-64">
          <Bar data={data} options={options} />
        </div>
      </div>
    </div>
  );
};

export default DietPlan;
