import React from 'react';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const DietPlan = () => {
  const weightData = {
    labels: ['Underweight', 'Normal weight', 'Overweight', 'Obese'],
    datasets: [
      {
        label: 'Recommended Weight Gain (kg)',
        data: [15, 13.75, 9.25, 7],
        backgroundColor: 'rgba(128, 0, 128, 0.6)',
      },
    ],
  };

  return (
    <div 
      className="container mx-auto px-4 py-8"
      style={{ 
        backgroundImage: 'url("/images/home-bg.jpg")',
        backgroundBlendMode: 'multiply',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundAttachment: 'fixed'
      }}
    >
      <h1 className="text-xl font-bold text-center text-purple-700 mb-6">
        Healthy Diet During Pregnancy
      </h1>
      
      <div className="bg-white p-6 rounded-lg shadow-lg">
        <h2 className="text-lg font-semibold text-purple-600">Healthy Weight Gain During Pregnancy</h2>
        <p>Steady weight gain is essential for the health of both mother and baby. However, excessive weight gain can lead to health risks.</p>

        <h3 className="text-md font-semibold text-purple-500 mt-4">Importance of Healthy Weight Gain</h3>
        <ul className="list-disc ml-6">
          <li>Supports babyâ€™s growth and development.</li>
          <li>Essential for maternal health and postpartum recovery.</li>
        </ul>

        <h3 className="text-md font-semibold text-purple-500 mt-4">Risks of Excess Weight Gain</h3>
        <ul className="list-disc ml-6">
          <li>Increases chances of gestational diabetes and high blood pressure.</li>
          <li>Can make it harder to lose weight after pregnancy.</li>
        </ul>

        <h3 className="text-md font-semibold text-purple-500 mt-4">Recommended Weight Gain (Based on BMI at Start of Pregnancy)</h3>
        <Bar data={weightData} options={{ responsive: true, maintainAspectRatio: false }} />

        <h3 className="text-md font-semibold text-purple-500 mt-4">Healthy Eating for Weight Management</h3>
        <ul className="list-disc ml-6">
          <li>Eat according to appetite without overeating.</li>
          <li>Focus on balanced nutrition (lean proteins, whole grains, fruits, and vegetables).</li>
          <li>Limit high-calorie, low-nutrient foods.</li>
        </ul>

        <h3 className="text-md font-semibold text-purple-500 mt-4">Monitoring Weight During Pregnancy</h3>
        <ul className="list-disc ml-6">
          <li>Regular weight check-ups with a healthcare provider.</li>
          <li>Consult a midwife, GP, dietitian, or obstetrician if unsure about weight gain.</li>
        </ul>
      </div>

      <p className="text-center mt-6 text-purple-700 font-semibold">
        Would you like a BMI calculator or weight tracker feature in the app?
      </p>
    </div>
  );
};

export default DietPlan;
