import React, { useState } from 'react';

interface InfoBoxProps {
  icon: string;
  title: string;
  content: string;
}

const InfoBox: React.FC<InfoBoxProps> = ({ icon, title, content }) => {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <div className="relative">
      <button
        onClick={() => setIsOpen(!isOpen)}
        className="p-3 bg-purple-100 rounded-lg hover:bg-purple-200 transition-colors w-full"
        title={title}
      >
        <span className="text-2xl">{icon}</span>
      </button>
      {isOpen && (
        <div className="absolute z-10 mt-2 p-3 bg-white rounded-lg shadow-lg w-48 left-1/2 transform -translate-x-1/2">
          <h4 className="font-semibold mb-2">{title}</h4>
          <p className="text-gray-600 text-sm">{content}</p>
        </div>
      )}
    </div>
  );
};

export default InfoBox;
