import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const Sidebar = () => {
  const location = useLocation();

  const navItems = [
    { path: '/period-health', label: 'Period Health' },
    { path: '/mental-wellness', label: 'Mental Wellness' },
    { path: '/sexual-health', label: 'Sexual & Reproductive Health' },
    { path: '/skin-hair-care', label: 'Skin & Hair Care' },
    { path: '/pregnancy-care', label: 'Pregnancy Care' },
  ];

  return (
    <div className="bg-white w-64 space-y-6 py-7 px-2 absolute inset-y-0 left-0 transform -translate-x-full md:relative md:translate-x-0 transition duration-200 ease-in-out">
      <Link to="/" className="text-2xl font-semibold text-center text-purple-600 flex items-center px-4">
        Swasthyashe
      </Link>
      <nav>
        {navItems.map((item) => (
          <Link
            key={item.path}
            to={item.path}
            className={`flex items-center space-x-2 py-2.5 px-4 rounded transition duration-200 ${
              location.pathname === item.path ? 'bg-purple-100 text-purple-700' : 'text-gray-700 hover:bg-purple-50'
            }`}
          >
            {item.label}
          </Link>
        ))}
      </nav>
    </div>
  );
};

export default Sidebar;
